var query = "select Shared_Solar_System__r.BWC_Project_Entity_Manual__r.Name Entity, MAX(Date__c) BillDate, SUM(Carry_Over_Balance__c) Total from System_Bill__c where Account_Bill__c = '{!Account_Bill__c.Account_Bill_ID__c}' group by Shared_Solar_System__r.BWC_Project_Entity_Manual__r.Name"


var result = sforce.connection.query(query); 
var records = result.getArray("records"); 

records= jQuery.map(records, function(n, i){
  return [[n.Entity, n.Total].join(": $")];
});

alert('This is the current amount this Account has due per this bill:\n\n' + records.join("\n\n"));
