CustomField.formula:
IF(OR(ISPICKVAL(Header_Status__c,"Error"),ISPICKVAL(Detail_Status__c, "Error")),"Error",
    IF(AND(ISPICKVAL(Header_Status__c,"Data Received"),ISPICKVAL(Detail_Status__c, "Data Received")),"Data Received",
        IF(AND(ISPICKVAL(Header_Status__c,"Complete"),ISPICKVAL(Detail_Status__c,"Complete")),"Complete",
            IF(OR(ISPICKVAL(Header_Status__c,"Data Received"),ISPICKVAL(Header_Status__c,"Complete"),ISPICKVAL(Detail_Status__c,"Data Received"),ISPICKVAL(Detail_Status__c,"Complete")),"Partial Data Received",
                IF(ISPICKVAL(Header_Status__c, "Sent"),"Sent",
                    IF(ISPICKVAL(Header_Status__c, "Pending"),"Pending","Error")
                )
            )
        )
    )
)
